var JSONPatch = require( __dirname + "/../src/index" );
var patchee = {
	a: true,
	b: 123,
	c: ["hello"]
};
var patcher = {
	a: false,
	b: 456,
	c: ["hello", "world"]
};
var patches = JSONPatch.generate( patchee, patcher );

console.log( "before:", patchee );
console.log( "patches:", patches );
JSONPatch.apply( patchee, patches );
console.log( "after:", patchee );
